
public class Pattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char i, j;
		
		for(i = 'A'; i <= 'E'; i++)
		{
			for(j = 'A'; j <= i; j++)
			{
//				System.out.print(j);
				System.out.print(i);
			}
			
			System.out.println();
		}

	}

}
